executionPlan.queryExpressions:
from health_predictor_request select member_id, cluster_id, cluster_instance_id, network_partition_id, health_description, value, stratos:concat(cluster_id, '-' ,cluster_instance_id) as health_predictor_cluster_network insert into health_predictor_concat;
from health_predictor_concat select cluster_id, cluster_instance_id, network_partition_id, health_predictor_cluster_network, health_description, value, "" as predicted_values insert into health_predictor_concat_temp;
define partition health_predictor_cluster_partition by health_predictor_concat_temp.health_predictor_cluster_network;
from health_predictor_concat_temp [health_description == 'memory_consumption'] #window.stratos:workloadPrediction(1 min, value,predicted_values) select cluster_id, cluster_instance_id, network_partition_id,value, predicted_values insert into predicted_memory_consumption_stats partition by health_predictor_cluster_partition;
from health_predictor_concat_temp [health_description == 'load_average'] #window.stratos:workloadPrediction(1 min, value,predicted_values) select cluster_id, cluster_instance_id, network_partition_id,value, predicted_values insert into predicted_load_average_stats partition by health_predictor_cluster_partition;
